import "./SearchBox.scss"

const SearchBox = props => {
    const { label,searchTerm,} = props;

    const capitalizedLabel = label[0].toUpperCase() + label.slice(1);
   //charlie -> C+ harlie = Charlie
    return (
      <form className="search-box">
        <label htmlFor={label} className="search-box__label">
          {capitalizedLabel}
        </label>
        <input type="text" name={label} value={searchTerm} onInput={handleInput} className="search-box__input" placeholder="SEARCH.."/>
      </form>
    );
  };

  export default SearchBox;
