import React from "react";
import beers from "../../data/beers";
import "./FilterItem.scss";


const FilterItems = ({ onChange, selected }) => {
  const items = ["High ABV( > 6.0%)", "Classic Range",  "Acidic ( ph < 4)"];

  const filterItemsJSX = beers.map((beer, index) => {
    return (
      <button key={"filter-options" + beer + index} value={beer}>
        {beer}
      </button>
    );
  });

  return (
    <form className="filter-options-form">
     <button className="filter-options_button"></button>
     <button className="filter-options_button"></button>
     <button className="filter-options_button"></button>
      <select name="filter-options" onChange={onChange} value={selected}>
        {filterItemsJSX}
      </select>
    </form>
  );
};
export default FilterItems;
